package chapter_3;

public class sec10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result = 0;
		result += 10; // result = 0+10
		System.out.println("result=" + result);
		result -= 5; //result = 10-5
		System.out.println("result=" + result);
		result *= 3; //result = 5x3
		System.out.println("result=" + result);
		result /= 5; // resulte = 15/3
		System.out.println("result=" + result);
		result %= 3; // result = 3/3 후 나머지 값
		System.out.println("result=" + result);
		

	}

}
